ValidationRule.errorConditionFormula:
AND(
    ISCHANGED( Status__c ), 
    NOT(
        OR(
            AND(
                TEXT(PRIORVALUE(Status__c)) = "Placed",
                OR(
                    TEXT(Status__c) = "In-Transit",
                    TEXT(Status__c) = "Delivered"
                )
            ),
            AND(
                TEXT(PRIORVALUE(Status__c)) = "In-Transit",
                TEXT(Status__c) = "Delivered"
            )
        )
    )
)
